mapper.select:
select problemNo, multipleChoice, title, uid, contents, starScore, categorySmallNo, type  
from problem
where problemNo = #{problemNo}

select categoryNo, categoryName
from categorysmall
where categoryNo = #{categorySmallNo}

select categoryNo, categoryName
from categorymedium
where categoryNo = #{categoryMediumNo}

select categoryNo, categoryName
from categorylarge
where categoryNo = #{categoryLargeNo}

select problemNo, title, uid, starScore 
from problem
where categorySmallNo = #{categorySmallNo}

mapper.update:
update problem
set multipleChoice = #{multipleChoice},title=#{title},uid=#{uid},contents=#{contents},categorySmallNo=#{categorySmallNo},type=#{type}
where problemNo = #{problemNo}
